import java.io.*;import java.util.*;public class OrganizandoEstoque{public static void main(String[]a)throws Exception{String s=new BufferedReader(new InputStreamReader(System.in)).readLine();if(s==null||s.isEmpty())return;Map<String,Integer>m=new LinkedHashMap<>();for(String t:s.split(",")){String[]p=t.split(":");m.put(p[0],m.getOrDefault(p[0],0)+Integer.parseInt(p[1]));}StringBuilder b=new StringBuilder();for(Map.Entry<String,Integer>e:m.entrySet())b.append(e.getKey()).append(":").append(e.getValue()).append(",");if(b.length()>0)b.setLength(b.length()-1);System.out.println(b);}}
