import java.io.*;public class SistemaDePontosDoCliente{public static void main(String[]a)throws Exception{String s=new BufferedReader(new InputStreamReader(System.in)).readLine();int i=s.indexOf(' '),i2=s.lastIndexOf(' ');String n=s.substring(0,i),p=s.substring(i+1,i2),c=s.substring(i2+1);String[]pa=p.split("\\.");int v=Integer.parseInt(pa[0])*100+Integer.parseInt(pa[1]);System.out.printf("Produto: %s | Categoria: %s | Preco: R$ %d.%02d%n",n,c,v/100,v%100);}}
